import mongoose from 'mongoose';
import URLSlug from 'mongoose-slug-generator';
const options = {};
mongoose.plugin(URLSlug, options);
import { parameterSchema } from './parameter.js';
import { controlSchema } from './control.js';
import { tagSchema } from './tag.js';
import { documentSchema } from './document.js';

const productSchema = new mongoose.Schema(
  {
    documents: [{ type: documentSchema, default: () => ({}) }],
    title: { type: String, default: 'Pralnicowirówka wolnostojąca wysokoobrotowa', },
    model: { type: String, unique: true, required: true, },
    icon: { type: String, default: '' },
    features: [{ type: String, default: '' }],
    body: { type: String, default: 'Ekstremalnie innowacyjna, ekstremalnie ekonomiczna i ergonomiczna, linia pralnicowirówek wolnostojących wysokoobrotowych FX spełnia najwyższe z możliwych wymagań stawianych współczesnym urządzeniom pralniczym. Zaprojektowana, aby osiągnąć wyjątkowo niskie zużycie wody i energii – w pełni pro ekologiczna, pełna nowoczesnych rozwiązań ( cascade drum, supereco powerwash, ultrabalance …). Gdy koszty wody i energii stale rosną, stanowi jedyny rozsądny wybór, gwarantując niski koszt prania przy jednoczesnym zachowaniu jego wysokiej jakości. Dodatkowo ciekawy i nowoczesny design podkreślający innowacyjność linii pralnicowirówek FX, to też trwałość, ergonomia oraz prosta obsługa i eksploatacja. Twoje oszczędności – czasu, wody i energii, to Twój dodatkowy zysk – z linią FX teraz możliwy.', },
    category: { ref: 'Category', type: mongoose.Schema.Types.ObjectId, required: [ true, 'Product always belongs to some category, so you must choose it', ], },
    tags: [{ type: tagSchema, default: () => ({}) }],
    image: { type: String, default: '', },
    parameters: [ { type: parameterSchema, default: () => ({}), }, ],
    controls: [ { type: controlSchema, default: () => ({}), }, ],
    wide: { type: Boolean, default: false, },
    coin: { type: Boolean, default: false, },
  },
  { timestamps: true }
);

productSchema.pre("save",async function(next){
  if(this.image === '' && this.wide) {

    this.image = 'data:image/png;base64,';
  } else if( this.image === '' && !this.wide){
   
    this.image='data:image/png;base64,';
  }
  if(this.icon === '' && this.wide) {
   
    this.icon = 'data:image/png;base64,';
  } else if( this.icon === '' && !this.wide){
    
    this.icon='data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAEAAAABcCAYAAADefbM+AAAAhUlEQVR42u3QAREAMAgEIF2M799Tc+yECHSSqcNeHSdAgAABAgQIECBAgAABAgQIECBAgAABAgQIECBAgAABAgQIECBAgAABAgQIECBAgAABAgQIECBAgAABAgQIECBAgAABAgQIECBAgAABAgQIECBAgAABAgQIECBAgAABAgQIECDgFwu1dwIFY5+gtwAAAABJRU5ErkJggg==';
  }
  next();
})

const Product = mongoose.model('Product', productSchema);

export default Product;
